SELECTITEMS.Definition .value( '(/GPReplica/ID)[1]', 'nvarchar(max)') AS "ID" ,ITEMS.Definition .value( '(/GPReplica/Name)[1]', 'nvarchar(max)') AS "Replica Name" ,STUFF(ITEMS.Definition .value( '(/GPReplica/ReplicaVersion)[1]', 'nvarchar(max)'),1,4,'') AS "Replica Version" ,ITEMS.Definition .value( '(/GPReplica/CreationDate)[1]', 'nvarchar(max)') AS "Creation Date",ITEMS.Definition .value( '(/GPReplica/LastSyncDate)[1]', 'nvarchar(max)') AS "Last Sync Date",ITEMS.Definition .value( '(/GPReplica/ServiceName)[1]', 'nvarchar(max)') AS "Service URL",ITEMS.Definitionfromsde.GDB_ITEMS AS ITEMS INNER JOIN sde.GDB_ITEMTYPES AS ITEMTYPESON ITEMS .Type = ITEMTYPES .UUIDWHERE ITEMTYPES. Name = 'Replica' AND ITEMS.Definition .value( '(/GPReplica/LastSyncDate)[1]', 'nvarchar(max)') is not null;
